import React, { useState } from "react";
import axios from "axios";
import "./App.css"; // CSS 파일을 임포트

function App() {
  // 사용자 입력을 저장할 상태 변수 설정
  const [inputValue, setInputValue] = useState("");

  // 입력값이 변경될 때 호출되는 함수
  const handleChange = (event) => {
    setInputValue(event.target.value); // 입력값 업데이트
  };

  // 입력값을 백엔드로 보내는 함수
  const handleSubmit = async (event) => {
    event.preventDefault(); // 기본 제출 동작 방지
    try {
      const response = await axios.post("http://localhost:5000/api", {
        input: inputValue, // 서버로 보내는 데이터
      });
      console.log(response.data); // 서버 응답 출력
    } catch (error) {
      console.error("서버로 데이터 전송 실패:", error);
    }
  };

  return (
    <div className="App">
      <h1>사용자 입력 받기</h1>
      <form onSubmit={handleSubmit} className="form-container">
        <input
          type="text"
          value={inputValue} // input 값은 상태로 관리
          onChange={handleChange} // 입력값이 변경될 때마다 호출되는 함수
          placeholder="여기에 입력하세요"
          className="input-field"
        />
        <button type="submit" className="submit-button">
          전송
        </button>
      </form>
      <p>입력한 값: {inputValue}</p>
    </div>
  );
}

export default App;
