import React, { useState } from "react";
import ReactMarkdown from "react-markdown"; // react-markdown 추가
import "./App.css"; // CSS 파일을 임포트

const STR = "Dimigo is the best highschool";
function App() {
  // 사용자 입력을 저장할 상태 변수 설정
  const [inputValue, setInputValue] = useState("");
  const [responseMessage, setResponseMessage] = useState(""); // 백엔드 응답 메시지 상태
  const [isLoading, setIsLoading] = useState(false); // 로딩 상태 추가

  // 입력값이 변경될 때 호출되는 함수
  const handleChange = (event) => {
    setInputValue(event.target.value); // 입력값 업데이트
  };

  // 입력값을 백엔드로 보내는 함수
  const handleSubmit = async (event) => {
    event.preventDefault(); // 기본 제출 동작 방지
    setIsLoading(true); // 요청 시작 시 로딩 활성화
    // 예시 데이터 추가
    const Exams = [
      {
        input:
          "@@Dimigo is the best highschool@@ + $$디미고는 최고의 고등학교이다$$",
        output: "정확합니다!",
      },
      {
        input:
          "@@Dimigo is the best highschool@@ + $$디미고는 훌륭한 고등학교이다$$",
        output: "정확합니다!",
      },
      {
        input: "@@Dimigo is the best highschool@@ + $$디미고는 좋은 학교이다$$",
        output:
          "비슷합니다! 하지만 best(최상급)의 느낌이 나타나면 좋으며, 그냥 학교보다는 고등학교가 더 정확한 번역입니다. 예시) 디미고는 최고로 좋은 고등학교이다.",
      },
      {
        input: "@@The cat jumped high@@ + $$그 고양이는 낮게 뛰었다$$",
        output:
          "틀렸습니다! 본 문장에서 high는 '높이', 혹은 '높게'라고 해석되어야 합니다. 옳은 해석) 그 고양이는 높게 뛰었다.",
      },
      {
        input: "@@He's playing a guitar@@ + $$그는 기타를 놀고 있다$$",
        output:
          "틀렸습니다! 일반적으로 play는 '놀다'라는 뜻으로 쓰이지만 본 문장에서는 '(악기를)연주하다' 라는 뜻으로 쓰였습니다. 옳은 해석) 그는 기타를 연주하고 있다.",
      },
    ];
    try {
      const response = await fetch("http://localhost:5000/api", {
        method: "POST", // POST 요청
        headers: {
          "Content-Type": "application/json", // JSON 형식으로 요청
        },
        body: JSON.stringify({
          system:
            "너는 사용자의 영어->한국어 번역이 맞는지 확인해주는 프로그램이고, 너의 답변은 한국어로만 이루어져야돼. 너에게는 영어 문장과 한국어 문장이 input으로 주어질거고, 영어 문장이 한국어 문장으로 제대로 번역되었는지 확인해야돼, 만약 잘못 번역했다면 개선점까지 출력해주어야돼",
          input: "@@" + STR + "@@ + $$" + inputValue + "$$",
          examples: Exams,
          system2: "대답은 한국어로",
        }), // 입력값을 JSON 형식으로 보내기
      });

      if (response.ok) {
        const data = await response.json(); // JSON 응답 받기
        setResponseMessage(data.message); // 서버 응답을 상태로 설정
      } else {
        setResponseMessage("서버 응답 오류");
      }
    } catch (error) {
      console.error("서버로 데이터 전송 실패:", error);
      setResponseMessage("서버로 데이터 전송 실패.");
    }
    setIsLoading(false); // 응답 받으면 로딩 비활성화
  };
  const handleKeyDown = (event) => {
    // 엔터키(13번) 눌렀을 때 줄바꿈 대신 전송
    if (event.key === "Enter") {
      event.preventDefault(); // 줄바꿈 방지
      handleSubmit(event); // 전송 함수 호출
    }
  };
  return (
    <div className="App">
      <div className="english-sentence-container">
        <p>{STR}</p> {/* 예시 문장 */}
      </div>
      <h1>위 문장의 뜻을 입력해보세요!</h1>
      <form onSubmit={handleSubmit} className="form-container">
        <textarea
          type="text"
          value={inputValue} // input 값은 상태로 관리
          onChange={handleChange} // 입력값이 변경될 때마다 호출되는 함수
          onKeyDown={handleKeyDown} // 엔터키 눌렀을 때 전송 처리
          placeholder="여기에 입력하세요"
          className="input-field"
        />
        <button type="submit" className="submit-button" disabled={isLoading}>
          {isLoading ? "로딩 중..." : "전송"}
        </button>
      </form>
      <div className="markdown-container">
        <div className="markdown-output">
          <ReactMarkdown>{responseMessage}</ReactMarkdown>
        </div>
      </div>
      {/* <p>백엔드 응답: {responseMessage}</p> 백엔드에서 받은 응답 출력 */}
    </div>
  );
}

export default App;
